// src/components/AlbumList.tsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AlbumList.css'; // Make sure this file exists

interface Album {
  id: string;
  title: string;
  artist: string;
  genre: string;
  releaseYear: number;
  coverImage: string;
}

const AlbumList: React.FC = () => {
  const [albums, setAlbums] = useState<Album[]>([]);

  useEffect(() => {
    const fetchAlbums = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/albums');
        setAlbums(response.data);
      } catch (error) {
        console.error('Error fetching albums:', error);
      }
    };

    fetchAlbums();
  }, []);

  return (
    <div className="album-list">
      <h2>My Vinyl Collection</h2>
      <ul>
        {albums.map((album) => (
          <li key={album.id} className="album">
            <img src={album.coverImage} alt={`${album.title} Cover`} className="album-cover" />
            <div className="album-info">
              <h3>{album.title}</h3>
              <p>Artist: {album.artist}</p>
              <p>Genre: {album.genre}</p>
              <p>Release Year: {album.releaseYear}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AlbumList;
